using UnityEngine;\n\npublic class CameraRevealSystem : MonoBehaviour {\n    public Camera mainCamera;\n    public float detectionDistance = 100f;\n    public LayerMask objectLayer;\n\n    void Update() {\n        DetectObjectsInFrustum();\n    }\n\n    private void DetectObjectsInFrustum() {\n        Plane[] planes = GeometryUtility.CalculateFrustumPlanes(mainCamera);\n        Collider[] colliders = Physics.OverlapSphere(mainCamera.transform.position, detectionDistance, objectLayer);\n\n        foreach (var collider in colliders) {\n            if (GeometryUtility.TestPlanesAABB(planes, collider.bounds)) {\n                // Object is in the camera's frustum\n                RevealObject(collider);\n            }\n        }\n    }\n\n    private void RevealObject(Collider collider) {\n        // Implement your object reveal logic here.\n        Debug.Log("Revealing object: " + collider.name);\n    }\n}